import { Component,	OnInit,	OnDestroy, ChangeDetectorRef, ViewChildren,	QueryList, ChangeDetectionStrategy,	ElementRef,	Input, AfterViewInit, ViewChild,} from "@angular/core";
import { FormGroup } from "@angular/forms";
import { XelerateService } from "src/shared/services/xelerate.service";
import { SummaryBaseClass } from "src/shared/templates/SummaryBaseClass.component";
import CustomerEntSummaryInfo from "./customerentsummary-info.json";
import { ContextProvider } from "src/shared/services/context-provider.service";
import { UtilityServices } from 'src/shared/services/utility.service';
@Component({
	selector: "app-customerentsummary",
	templateUrl: "./customerentsummary.component.html",
	providers: [ContextProvider],
	changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomerEntSummary extends SummaryBaseClass {
    name: string = "CustomerEntSummary";
	constructor(
		public xelerate: XelerateService,
		public cp: ContextProvider,
		public changeDetectorRef: ChangeDetectorRef,
		public elmRef: ElementRef
	) {
		super(xelerate, cp, changeDetectorRef, elmRef);
		this.metaInfo = UtilityServices.copyJSON(CustomerEntSummaryInfo);
	}
	ngAfterViewInit() {
		this.personalizeView();
		this.xelerate.setValue("userLanding",this.xelerate.data.transferData.get("userId"));
		this.xelerate.addStickyHeader();
		this.xelerate.propagateChangeDetection(this);
	}
	async personalizeView() {
		UtilityServices.stopLoader(document.getElementsByClassName("xelerate-wrapper")[0]);
		this.prefs = await this.xelerate.server.getContent({ url: "assets/" + this.name + "_prefs.json" });
		this.xelerate.personalizeWidgets(this);
	}
}
